css:
/*pc*/@media (min-width: 601px){
.top-bar__countdown {
    font-size: 15px;background:#878787;
    color: #fff;
}
  .top-bar {
    background-color: #a9a9a9;
}
.top-bar__page-links a {
    color: #ffffff;
    font-size: 14px;
    font-weight: 500;
}
  .header__logo {
    max-height: 200px;
    max-width: 150px;
  }
  .top-bar__nav a {
    color: #000000;
    font-size: 13px;
}
  .header__page-links a {
    color: #000000;
    font-size: 13px;
    font-weight: 500;
}
  .top-bar {
    background-color: #ededed;
}
  .i-facebook {
    color: #000;
    font-size: 20px;
}
  .i-instagram {color: #000;font-size: 20px;}
  .header__buttons .btn {font-size: 30px;}
  .header__buttons .badge-primary {background-color: #000000;}
 .products-carousel__title {color: #000;
    font-family: Montserrat,sans-serif;font-size: 28px;}
  .product-card__name {font-size: 18px;
    font-family: Montserrat,sans-serif;margin-bottom: 9px;}

      .header__nav a:hover {background: transparent;
      font-weight:700;font-family:  Montserrat,sans-serif;
 text-decoration: none;color: #000000;margin-bottom: 15px;}

  .header__nav--full:before {
    background: #fff;}
  .header__nav--full {
    padding-top: 3px;
}

 }
 /*mobile*/@media (max-width: 600px) {
   .top-bar{display:none;}
.header__logo {max-height: 100px;max-width: 70px;}
   .products-carousel__title{color: #000;
    font-family: Montserrat,sans-serif;font-size: 20px;}
  .product-card__name {
    font-size: 16px;font-family: Montserrat,sans-serif;
    margin-bottom: 9px;
}

}
 /*responsivo*/
.cart-count{background-color: #000000;}
.info-bar__title {
    font-family:Montserrat,sans-serif;
    font-size: 18px;
    font-weight: 700;
    margin-left: 0;}
.info-bar__item{   color: #000000;}
.i-shipping-fast{font-size:20px;}
.i-credit-card{font-size:20px;}
.footer {background-color: #3e3e3e;
    border-color: #424242;
    color: #fff;}
.footer__contacts a {color: #fff;font-size: 14px;}
.footer__links a {color: #fff;font-size: 14px;}
.footer p {font-size: 16px;}
.footer__social i {color: #fff;font-size: 23px;}
.ecom-credits {background-color: #0000;padding-right: 6px;}
.product__short-stock {display:none;}
 .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
.menu__item {
    align-items: center;
  border-bottom: var(--border-width) solid #ebebeb;}
.menu__item a:hover { background: #000;color: #fff; 
  font-weight:700;font-family: Montserrat,sans-serif;
text-decoration: none; border-bottom:none;}
.menu__list a:hover {color:#000;font-weight: 700;
  font-family: Montserrat,sans-serif;}
 .header__search {
    border-bottom: 1px solid #000000;
}
.header__buttons .badge-primary {
    background-color: #000000;
    border-radius: 50%;
    margin-right: 3px;
    margin-top: 10px;
}
.breadcrumb-item.active {
    color: #000000;
}
.page-title__head{font-family: Montserrat,sans-serif;}

