css:
/*pc*/@media (min-width: 601px){
.top-bar__countdown {
    font-size: 15px;background:#878787;
    color: #fff;
}
  .top-bar {
    background-color: #a9a9a9;
}
.top-bar__page-links a {
    color: #ffffff;
    font-size: 14px;
    font-weight: 500;
}
  .header__logo {
    max-height: 200px;
    max-width: 150px;
  }
  .top-bar__nav a {
    color: #000000;
    font-size: 13px;
}
  .top-bar {
    background-color: #ededed;
}
  .i-facebook {
    color: #000;
    font-size: 20px;
}
  .i-instagram {
    color: #000;
    font-size: 20px;
}
  .header__buttons .btn {
    font-size: 30px;}
  .header__buttons .badge-primary {
    background-color: #000000;}
  .header__row--centralized-logo .header__buttons-block {
    display: flex;
    flex-basis: 45%;
    justify-content: flex-end;
    max-width: 28%;
}
 }
 /*mobile*/@media (max-width: 600px) {
   .top-bar{display:none;}
.header__logo {
    max-height: 100px;
    max-width: 70px;
  }

}
 /*responsivo*/
.info-bar__item div .info-bar__title {
    font-family: Montserrat,sans-serif;
    font-size: 18px;
    font-weight: 700;
    margin-left: 0;
}
.info-bar__item div {
    font-size: 15px;
    font-weight: 500;
    margin-left: 0;
    text-transform: none;}
.info-bar__item {color: #000000;}
.i-shipping-fast{font-size:20px;}
.i-credit-card{font-size:20px;}
.footer {
    background-color: #3e3e3e;
    border-color: #424242;
    color: #fff;
}
 .info-bar {
    background-color: #fff;
    box-shadow: 0 1px 0 rgb(255 255 255 / 10%);
}
.footer__contacts a {
    color: #fff;
    font-size: 14px;
}
.footer__links a {
    color: #fff;
    font-size: 14px;
}
.footer p {
    font-size: 16px;
}
.footer__social i {
    color: #fff;
    font-size: 23px;
}
.ecom-credits {
    background-color: #0000;
    padding-right: 6px;
}

